import React, { Component } from 'react';
import TableData from './TableData';
import Data from './Data.json';


class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dataUser : Data
    }
  }
    render() {

        return (
            <div className="container">
              <h4 className="text"> Demo Add | Edit | Delete </h4>
              <div className="input-group mb-3">
                  <input type="text" className="form-control" placeholder="Search" aria-label="Recipient's username" aria-describedby="basic-addon2" />
                    <div className="input-group-append"><button className="btn btn-outline-secondary" type="button">Button</button>
                  </div>
              </div>  
              <br/>
                <table className="table table-bordered">
                      <thead>
                        <tr>
                          <th>Firstname</th>
                          <th>Lastname</th>
                          <th>Email</th>
                          <th>Address</th>
                          <th> System</th>
                        </tr>
                      </thead>
                        <tbody>
                          <TableData dataUserJson = {this.state.dataUser}/>
                       </tbody>
                 </table> 

              <div className="card" style={{width: '18rem'}}>
                <div className="card-header">  Add</div>
                    <div className="form-group">
                    <input type="text" className="form-control" placeholder="ten" />
                    <input type="text" className="form-control" placeholder="ten" />
                    <input type="text" className="form-control" placeholder="ten" />
                    <button type="submit" className="btn btn-success">submit</button>
                  </div> 
                  </div>
               </div>    
        );
      }
}

export default Home;
